#include <iostream>
#include <string> 

using namespace std;
string ordinal(int n) {
string o[] = { "th", "st", "nd", "rd" };
int x = n % 100;
int value = x - 20 % 10;

if (value < 4) {
return o[value];
}

if (x < 4) {
return o[x];
}

return o[0];
}

string format(int number) {
string colors[] = { "Blue", "Green", "Red", "Orange", "Violet", "Indigo", "Yellow" };
string ordinalNum = ordinal(number);
return to_string(number) + ordinalNum + " choice is " + colors[number - 1];
}

