import java.util.ArrayList;

public class Main {
  public static ArrayList<Integer> concatArr(int[] arr1, int[] arr2){
        ArrayList<Integer> result = new ArrayList<>();
        for (int j : arr1) {
            result.add(j);
        }
        for (int j : arr2) {
            result.add(j);
        }
        return result;
  }
