import java.util.ArrayList;

public class Main {
  public static ArrayList<Integer> concatArr(int[] arr1, int[] arr2){
        ArrayList<Integer> result = new ArrayList<>();
        for (int j : arr1) {
            result.add(j);
        }
        for (int j : arr2) {
            result.add(j);
        }
        return result;
  }
}

public class Main {
public static int multiply(int[] numbers) {
int value = 1;
for (int number : numbers) {
value = value * number;
}
return value;
}

class NumbersPair {
private int firstNumber;
private int secondNumber;

public NumbersPair(int firstNumber, int secondNumber) {
this.firstNumber = firstNumber;
this.secondNumber = secondNumber;
}

public int multiply() {
return this.firstNumber * this.secondNumber;
}
}

public class Main {
static public int multiplyPairOfNumbers(NumbersPair pair) {
return pair.multiply();
}
