multiply <- function(numbers) {
val <- 1
for (number in numbers) {
val = val * number
}
return(val)

format <- function(n) {
colors = c("Blue", "Green", "Red", "Orange", "Violet", "Indigo", "Yellow")
Ordinal = function(n) {
o = c("th","st","nd","rd")
x = n %% 100
if((x-19) %% 10 <= 4) {
return(paste(x, (o[(x-19) %% 10]), sep=""))
}
if (x + 1 <= 4) {
return(paste(x, (o[x + 1]), sep=""))
}

return(paste(x, o[1], sep=""))
}
return(paste(toString(Ordinal(n)), "choice is", colors[n]))
